-- Lua cmdparse module---- Handles parsing of commands from std input--local M = {}-- JumpTableM.cbt = {}function M:init(test) print(test)end-- Set the callback tablefunction M:setCbTable(cbt) if cbt ~= nil then  M.cbt = cbt endend-- Add callback to callback tablefunction M:addCb(name, cb) local newentry = { name, cb } table.insert(M.cbt, newentry)endfunction M:getJT() -- Iterate over all command entries for i,v in ipairs(M.cbt) do  print("Command: "..v["txt"]) end end -- Parse input stringfunction M:parse(instring) print("String"..instring)endreturn M
